package ControlStatement;

public class Controlstatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//if else statement
		int num=19;
		if(num%2==0)
		{
			System.out.println("Number is even");
		}
		else
		{
			System.out.println("Number is odd");
		}
		
	}

}
