package ControlStatement;

public class Nestedif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		if(false)
		{
			if(true)
			{
				System.out.println("XYZ");
			}
			else
			{
				System.out.println("ABC");
			}
		}
		else
		{
			if(true)
			{
				System.out.println("123");
			}
			else
			{
				System.out.println("890");
			}
		}
	}

}
