package JavaBasic;

public class Myoperatorspractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x=50;
int y=x++;
System.out.println(y);//Value of y would be 50 as value of x will be used first then will be incremented
System.out.println(x);//updated 51
int z=--x;
System.out.println(z);//50
System.out.println(x);//50
	}

}
