package JavaBasic;

public class Operator2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	//Assignment Operator:=,+=
		int x=50;//= is assignment operator
		x+=10;//x=x+10
		System.out.println(x); //60
		x-=5;//x=x-5
		System.out.println(x);//55
		x*=6;//x=x*6
		System.out.println(x);//330
		x/=7;//x=x/7
		System.out.println(x);//47
		x%=9;//x=x%9
		System.out.println(x);//2
//		Ternary Operator It is represented by ?:
		int y=20,z=30;
		String a=y>z?"y is greatest":"z is greatest";
		System.out.println(a);
		

	}

}
